mapper.select:

	SELECT R.OT_ID
	FROM RESERVATION R, (SELECT OT_ID
	                     FROM OPENING_TIME
	                     WHERE DR_ID = #{drId}
	                     AND YEAR_DATE LIKE #{date}
	                     ORDER BY YEAR_DATE, TO_NUMBER(TIMETABLE_ID)
	                     ) O
	WHERE R.OT_ID = O.OT_ID
	ORDER BY TO_NUMBER(R.OT_ID)
