mapper.update:
UPDATE REFERRAL
RECEIVE_CHK = '2' RECEIVE_CHK = '3' RECEIVE_CHK = '4'
WHERE REFER_ID = #{referId}

mapper.select:
SELECT COUNT(*) FROM REFERRAL
WHERE S_DR_ID = #{drId}
AND REFER_CONTENT LIKE '%'||#{searchKeyword}||'%'

SELECT COUNT(*) FROM REFERRAL
WHERE R_DR_ID = #{drId}
AND REFER_CONTENT LIKE '%'||#{searchKeyword}||'%'
