mapper.select:

	SELECT * 
	FROM OPENING_TIME
	WHERE DR_ID = #{drId}
	AND YEAR_DATE LIKE #{date}
	ORDER BY YEAR_DATE, TO_NUMBER(TIMETABLE_ID)

mapper.update:
	
		UPDATE OPENING_TIME SET OT_STATE = #{status}
		WHERE DR_ID = #{drId}
		AND YEAR_DATE = #{date}
		AND TIMETABLE_ID IN
		
    		#{timeId}
